    }if(type==='totrinhgui'){
       url = urlAPI.totrinhGuiGetallPaging + `key=&status=-1&staffid=${staffId}&xem=-1&page=${page}&size=30`
    }
  let hh =POST('http://103.114.104.246:8080/api-cargo-cs/api/login', 
    {
      userName:"admin",
      password:"12345"
    })
    const url = LinkApi.order_getall; 
    await fetch(url)
        .then(res => res.json()) 
        .then(res => {
         console.log(res);
        })
        .catch(error => {
          console.log(error);
        });

    /*if (res!==null) {
      setLoading(false);
      setRefreshing(false);
      setData(page === 1 ? res : [...data, ...res]);
      setpagesize(res.length)}else{
      setLoading(false)
    }
    //


  /*  const url = `https://5f8abf3a84531500167061c7.mockapi.io/api/vbd/trichYeu?page=${page}&limit=1`;
    setLoading(true);
   // fetch(props.url)
    fetch(url)
        .then(res => res.json())
        .then(res => {
         // alert(res.length)
          setLoading(false);
          setRefreshing(false);
          setError(res.error || null);
          setData( page === 1 ? res : [...data, ...res]);
          setpagesize(res.length)
            //alert(data)
        })
        .catch(error => {
           setLoading(false)
        });*/
};

  return (
    <Card
      className={clsx(classes.root, className)}
      {...rest}
    >
      <CardHeader title="Đơn hàng trong ngày" />
      <Divider />
      <PerfectScrollbar>
        <Box minWidth={800}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>
                  Đơn hàng
                </TableCell>
                <TableCell>
                  Khách hàng
                </TableCell>
                <TableCell sortDirection="desc">
                  <Tooltip
                    enterDelay={300}
                    title="Sort"
                  >
                    <TableSortLabel
                      active
                      direction="desc"
                    >
                      Giá
                    </TableSortLabel>
                  </Tooltip>
                </TableCell>
                <TableCell>
                  Tình trạng
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {data.map((order) => (
                <TableRow
                  hover
                  key={order.id}
                >
                  <TableCell>
                    {order.ID}
                  </TableCell>
                  <TableCell>
                    {order.CustomerName}
                  </TableCell>
                  <TableCell>
                    {order.PriceTotal}
                  </TableCell>
                  <TableCell>
                    {order.IsConNo===1?'Chưa thanh toán':'Đã thanh toán'}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
      </PerfectScrollbar>
    </Card>
  );
};

LatestOrders.propTypes = {
  className: PropTypes.string
};

export default LatestOrders;
