import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import moment from 'moment';
import PerfectScrollbar from 'react-perfect-scrollbar';
import {
  Avatar,
  Box,
  Card,
  Link,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TablePagination,
  TableRow,
  Typography,
  makeStyles,
  MenuItem,
  Menu,
  Drawer,
  Button,
  TextField, FormControlLabel,
  DialogTitle, DialogContent, DialogContentText, DialogActions, Dialog, Grid, TableContainer, Paper, CardContent
} from '@material-ui/core';
import { SearchIcon } from '@material-ui/data-grid';
import IconButton from '@material-ui/core/IconButton';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import DeleteIcon from '@material-ui/icons/Delete';
import FullScreenDialog from 'src/views/layout_docs/air_export/FreightListView/HAWB/FullScreenDialog';
import PrintCenterDialog from 'src/views/layout_docs/air_export/FreightListView/HAWB/PrintCenterDialog';
import { LinkApi } from 'src/utils/api/api-link';
import { GET, POST } from 'src/utils/api/method';
import PrintMAWB from 'src/views/layout_docs/air_export/FreightListView/PrintMAWB';
import { InputLabelCustom } from 'src/components/custom/inputtxt';
import PrintInvoice from './PrintInvoice';
import MAWB from './MAWB';

const useStyles = makeStyles((theme) => ({
  root: {},
  avatar: {
    marginRight: theme.spacing(2)
  },
  table:{
    width: 600
  }
}));
const ITEM_HEIGHT = 48;

const Results = ({ className, newaddedOrder, customers, ...rest }) => {
  const classes = useStyles();
  const [selectedCustomerIds, setSelectedCustomerIds] = useState([]);
  const [limit, setLimit] = useState(5);
  const [page, setPage] = useState(0);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const openMenu = Boolean(anchorEl);
  const [opendialog, setOpendialog] = React.useState(false);
  const [data, setData] = useState([]);
  const [selectedItem, setSelectedItem] = useState([]);
  const [opensides, setopenside] = useState(false);
  const [selectedMenuItem, setSelectedMenuItem] = useState(null);
  const [searchDate, setsearchDate] = useState(moment(new Date(), 'YYYY-MM-DD').format().substring(0, 10));
  const [openViewChange, setopenViewChange] = React.useState(false);
  const [lichsuthaydoi, setlichsuthaydoi] = useState([]);

  useEffect(() => {    
    makeRemoteRequest();
  }, []); 
  const makeRemoteRequest = async () => {
    let res=await GET(LinkApi.order_getBydate + searchDate)
    if (res!==undefined) {
      setData(res)
     // setLoading(false);
      //setRefreshing(false);
      //setData(page === 1 ? res : [...data, ...res]);
      //setpagesize(res.length)}else{
      //setLoading(false)
    }
    /*setLoading(true)
    const staffId =await AsyncStorage.getItem('staffId')
     var url=''
    if(type==='totrinhnhan'){
      url = urlAPI.totrinhNhanGetallPaging + `key=&status=-1&staffid=${staffId}&xem=-1&page=${page}&size=30`
    }if(type==='totrinhgui'){
       url = urlAPI.totrinhGuiGetallPaging + `key=&status=-1&staffid=${staffId}&xem=-1&page=${page}&size=30`
    }
  let hh =POST('http://103.114.104.246:8080/api-cargo-cs/api/login', 
    {
      userName:"admin",
      password:"12345"
    })
    const url = LinkApi.order_getall; 
    await fetch(url)
        .then(res => res.json()) 
        .then(res => {
         console.log(res);
        })
        .catch(error => {
          console.log(error);
        });

    /*if (res!==null) {
      setLoading(false);
      setRefreshing(false);
      setData(page === 1 ? res : [...data, ...res]);
      setpagesize(res.length)}else{
      setLoading(false)
    }
    //


  /*  const url = `https://5f8abf3a84531500167061c7.mockapi.io/api/vbd/trichYeu?page=${page}&limit=1`;
    setLoading(true);
   // fetch(props.url)
    fetch(url)
        .then(res => res.json())
        .then(res => {
         // alert(res.length)
          setLoading(false);
          setRefreshing(false);
          setError(res.error || null);
          setData( page === 1 ? res : [...data, ...res]);
          setpagesize(res.length)
            //alert(data)
        })
        .catch(error => {
           setLoading(false)
        });*/
};

  const getlichsu = async (item) => {
    let res=await GET(LinkApi.order_getLichSu + item.Code)
    if (res!==undefined) {
      setlichsuthaydoi(res)
    }
  };


  const handleSelectAll = (event) => {
    let newSelectedCustomerIds;

    if (event.target.checked) {
      newSelectedCustomerIds = data.map((item) => item.id);
    } else {
      newSelectedCustomerIds = [];
    }

    setSelectedCustomerIds(newSelectedCustomerIds);
  };

  const handleSelectOne = (event, id) => {
    const selectedIndex = selectedCustomerIds.indexOf(id);
    let newSelectedCustomerIds = [];

    if (selectedIndex === -1) {
      newSelectedCustomerIds = newSelectedCustomerIds.concat(selectedCustomerIds, id);
    } else if (selectedIndex === 0) {
      newSelectedCustomerIds = newSelectedCustomerIds.concat(selectedCustomerIds.slice(1));
    } else if (selectedIndex === selectedCustomerIds.length - 1) {
      newSelectedCustomerIds = newSelectedCustomerIds.concat(selectedCustomerIds.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelectedCustomerIds = newSelectedCustomerIds.concat(
        selectedCustomerIds.slice(0, selectedIndex),
        selectedCustomerIds.slice(selectedIndex + 1)
      );
    }
    alert(newSelectedCustomerIds)
    setSelectedCustomerIds(newSelectedCustomerIds);
  };

  const handleLimitChange = (event) => {
    setLimit(event.target.value);
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const handleClick = ({event}) => {
    console.log(event)
    setAnchorEl(event.currentTarget);
  };


  const handleCloseLongMenu = () => {
    setAnchorEl(null);
  };
  const DeleteItem = ({orderz}) => {
    return <div onClick={() => alert(orderz.ID)}>Delete</div>
  };
  const deleteItem = async () => {
    //setAnchorEl(null);
    //alert(selectedDeleteItem)
    await GET(LinkApi.order_del+selectedMenuItem.ID)
        .then(respon => {
          console.log("respon:", respon)
          if (respon==="SUCCESS") {
           window.location.reload()
           setAnchorEl(null);
          }
        })
        .catch(error => console.log(error));
      };

  const Payment = ({item}) => {
    const [open, setOpen] = React.useState(false);
    const [openDetails, setOpenDetails] = React.useState(false);
    const [lastRefresh, setlastRefresh] = useState(Date(Date.now()).toString());
    const [sotienthanhtoan, setsotienthanhtoan] = useState(null);
    const [hinhthucthanhtoan, sethinhthucthanhtoan] = useState('Tiền mặt');
    const [dathanhtoan, setdathanhtoan] = useState('');
    const [conno, setconno] = useState('');
    const [ghichu, setghichu] = useState('');
    const [itemPayed, setitemPayed] = useState([]);
    useEffect(() => {   
      console.log(item) 
      getItemPayment();
    }, []); 
    const getItemPayment = async () => {
      await GET(LinkApi.order_getByOrderId+item.ID)
        .then(respon => {
          setitemPayed(respon)
          //setlistPay(respon);
          //setlastRefresh(Date(Date.now()).toString()) 
          let tongdathanhtoan=0;
          let tienno=0;
          for (let i=0; i< respon.length; i++) {
            tongdathanhtoan=respon[i].Amount+tongdathanhtoan;
            tienno=item.PriceTotal-tongdathanhtoan;
          }
          setdathanhtoan(tongdathanhtoan)
          setconno(tienno);
          //console.log("OK", respon) 
        });
      //  
    }    

    const paymentDetails = () => {

    }
    const handleSave = () => {
      const infoPayment={
        "OrderId": item.ID,
        "PaymentType": hinhthucthanhtoan==='Tiền mặt' ? 1 : 0,
        "Amount": sotienthanhtoan,
        "PayDate": moment(new Date(), 'DD-MM-YYYY').format().substring(0, 10)+" "+ moment(new Date(), 'DD-MM-YYYY').format().substring(11, 19),
        "Cashier": "Bưởi",
        "Created": moment(new Date(), 'DD-MM-YYYY').format().substring(0, 10)+" "+ moment(new Date(), 'DD-MM-YYYY').format().substring(11, 19),
        "CreatedBy": 1,
        "Modified": null,
        "ModifiedBy": 0,
        "Note": ghichu,
        "OldId": ""
        };
        let newPay= POST(LinkApi.pay_add, infoPayment)
      .then(responsez => window.location.reload())
      .catch(error => alert('error', error))
      if (Number(sotienthanhtoan) >= Number(conno)) {
        updateOrder('hetno')
      }
      //setOpen(false);
    };
    const updateOrder = async (string) => {
      const infoOrder={
        "ID":item.ID,
        "Code": item.Code,
        //"CustomerId": 1,
        "CustomerName": item.CustomerName,
        "CustomerPhone": item.CustomerPhone,
        "CustomerAddress": item.CustomerAddress,
        "UserId": 1,
        "Description": item.Description,
        "LoiChuc": item.LoiChuc,
        "PriceTotal": item.PriceTotal,
        "IsConNo": string==='hetno'?'0':'1',
        "ShipEstimate": item.ShipEstimate,
        "ShipThat": item.ShipThat,
        "DeliveryNote": item.DeliveryNote,
        "Created": item.Created,
        "CreatedBy": item.CreatedBy,
        "Modified": item.Modified,
        "ModifiedBy": item.ModifiedBy,
        "Note": item.Note,
        };
        let newOrder= await POST(LinkApi.order_update, infoOrder)
        .then(response => {
          alert("luu thong tin thanh cong")
        })
        .catch(error => console.log('error2', error))  
    };
  const httt=[{ key: 1, ht: "Tiền mặt"}, {key: 2, ht: 'Chuyển khoản'}]
    return (
      <div>
        <Dialog style={{width: 800}} open={openDetails} onClose={() => setOpenDetails(false)} aria-labelledby="form-dialog-title">
          <DialogTitle id="form-dialog-title">Thanh toán đơn hàng </DialogTitle>
          <DialogContent>
            <DialogContentText>
              Các khoản thanh toán cho đơn hàng số {item.OrderID} của khách hàng {item.CustomerName}
            </DialogContentText>
            <TableContainer component={Paper}>
              <Table aria-label="simple table">
                <TableHead>
                  <TableRow>
                    <TableCell>Số tiền</TableCell>
                    <TableCell align="center">Thu ngân</TableCell>
                    <TableCell align="center">Ngày</TableCell>
                    <TableCell align="center">Người tạo</TableCell>
                    <TableCell align="center">Ghi chú</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {itemPayed.map((row) => (
                    <TableRow key={row.ID}>
                      <TableCell component="th" scope="row">
                        {row.Amount.toLocaleString()}
                      </TableCell>
                      <TableCell align="right">{row.Cashier}</TableCell>
                      <TableCell align="right">{moment(row.Created).format('HH:MM')} - {moment(row.Created).format('DD/MM/YYYY')}</TableCell>
                      <TableCell align="right">{row.CreatedBy}</TableCell>
                      <TableCell align="right">{row.Note}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenDetails(false)} color="secondary">
              Thoát
            </Button>
          </DialogActions>
        </Dialog>

        <Dialog open={open} onClose={() => setOpen(false)} aria-labelledby="form-dialog-title">
          <DialogTitle id="form-dialog-title">Thanh toán đơn hàng </DialogTitle>
          <DialogContent>
            <DialogContentText>
              Thanh toán cho đơn hàng số {item.OrderID} của khách hàng {item.CustomerName}
            </DialogContentText>
            <DialogContentText>
              Số tiền còn nợ: {conno.toLocaleString()} đ &nbsp;&nbsp;&nbsp;&nbsp;
              <Link href={"#"} onClick={() => setsotienthanhtoan(conno)}>Copy</Link>
            </DialogContentText>
            <Grid container spacing={1} justify="center" alignItems="center">        
              <Grid item xs={12} style={{ backgroundColor: '', borderRadius: 4 }}>        
                <Grid item xs={12} style={{ height: 250, backgroundColor: '', paddingTop: 10, borderRadius: 4 }}>
                  <Grid item xs={12} style={{ border: '1px solid grey', padding: 1, backgroundColor: 'white', height: 160, borderRadius: 4 }}>
                    <InputLabelCustom label={'Số tiền'} value={sotienthanhtoan} onChange={e => setsotienthanhtoan(e.target.value)} 
                      customstyle={null} multiline={false} rowsMax={1} type="number" /> 
                    <FormControlLabel
                      fullWidth
                      //className={classes.formControl}
                      label={<Typography style={{ marginLeft: 10, width: 110, fontFamily: 'sans-serif', fontSize: 12, alignSelf: "flex-start", paddingLeft: 0, textAlign: 'start', }}>Hình thức thanh toán</Typography>}
                      style={{ margin: 5, }}
                      labelPlacement="start"
                      control={(             
                        <TextField
                          select
                          size="small"
                          InputProps={{ style: { fontSize: 14, backgroundColor: 'white' } }}
                          style={{ width: 250, marginLeft: 10, fontSize: 14, backgroundColor: 'white' }}
                          //value={httt}
                          value={hinhthucthanhtoan}
                          onChange={e => sethinhthucthanhtoan(e.target.value)} 
                          variant="outlined"
                        >
                          {httt.map((option) => (
                            <MenuItem key={option.key} value={option.ht}>
                              {option.ht}
                            </MenuItem>
                          ))}
                        </TextField>
                      )}
                    />
                    <InputLabelCustom label={'Ghi chú'} value={ghichu} onChange={e => setghichu(e.target.value)} 
                      customstyle={null} multiline={false} rowsMax={1} />
                  </Grid>
                </Grid>
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions style={{ border: '1px solid grey', flexDirection: 'space-between'}}>
            <Button onClick={() => setOpen(false)} color="secondary">
              Cancel
            </Button>
            <Button onClick={handleSave} color="primary">
              Save
            </Button>
          </DialogActions>
        </Dialog>
        <Typography
          style={{ fontSize: 12 }}
          color="textPrimary"
          variant="body1"
        >
          Ship thật: {item.ShipThat.toLocaleString(undefined, {maximumFractionDigits:2})} đ
        </Typography>
        <Link
          href={'#'}
          style={{ fontSize: 12, color: 'green' }}
          color="textPrimary"
          variant="body1"
          onClick={() => setOpenDetails(true)}
        >
          Đã thanh toán: {dathanhtoan.toLocaleString(undefined, {maximumFractionDigits:2})} đ
        </Link>
        <Typography
          style={{ fontSize: 12, color: 'red' }}
          color="textPrimary"
          variant="body1"
        >
