import React, { useState} from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import {
  Box,
  Button,
  Card,
  CardContent,
  TextField,
  InputAdornment,
  SvgIcon,
  makeStyles,
  Drawer
} from '@material-ui/core';
import { Search as SearchIcon } from 'react-feather';
import MAWB from './MAWB';

let Connection = require('tedious').Connection;

const useStyles = makeStyles((theme) => ({
  root: {},
  importButton: {
    marginRight: theme.spacing(1)
  },
  exportButton: {
    marginRight: theme.spacing(1)
  }
}));



const Toolbar = ({ className, handleAddOrderToindexJS, ...rest }) => {
  const classes = useStyles();
  const [opensides, setopenside] = useState(false);

  const list = (anchor) => (
    <div
      className={clsx(classes.list, {
        [classes.fullList]: anchor === 'top' || anchor === 'bottom',
      })}
      role="presentation"
   //   onClick={setopenside(false)}
   //   onKeyDown={setopenside(false)}
    >
      <MAWB type={'newMAWB'} handleAddOrder={handleAddOrder} />
    </div>
  );
  const handleAddOrder = (item) => {
    handleAddOrderToindexJS(item);
  }
  async function connsql() {
    const config = {
      server: '103.114.104.246',
      options: {
        database: "CARRGO_CS"
      },
      authentication: {
        type: 'default',
        options: {
          userName: 'sa',
          password: 'Stone1234'
        }
      }
    };

    const connection = new Connection(config);
    connection.on('connect', async function (err) {
      if (err) {
        alert('Error: ', err);
      }
      let request = await new Request("select 42, 'hello world'", function (errs, rowCount) {
        if (errs) {
          alert(errs);
        } else {
          alert(rowCount + ' rows');
        }
      });

      request.on('row', function (columns) {
        columns.forEach(function (column) {
          //console.log(column.value);
        });
      });

      connection.execSql(request);
    });
  }
  return (
    <div
      className={clsx(classes.root, className)}
      {...rest}
    >
      <Drawer anchor="right" open={opensides} onClose={() => setopenside(false)}>
        {list('')}
      </Drawer>
      <Box
        display="flex"
        justifyContent="flex-end"
      >
        <Button
          color="primary"
          variant="contained"
          //onClick={() => setopenside(true)}
          onClick={() => setopenside(true)}
        >
          Thêm đơn hàng
        </Button>
      </Box>
      <Box mt={3}>
        <Card>
          <CardContent>
            <Box maxWidth={500}>
              <TextField
                fullWidth
                InputProps={{
                  startAdornment: (
                    <InputAdornment  position="start">
                      <SvgIcon
                        fontSize="small"
                        color="action"
                      >
                        <SearchIcon />
                      </SvgIcon>
                    </InputAdornment>
                  )
                }}
                placeholder="Search customer"
                variant="outlined"
              />
            </Box>
          </CardContent>
        </Card>
      </Box>
    </div>
  );
};

Toolbar.propTypes = {
  className: PropTypes.string
};

export default Toolbar;
